(function ($) {
    $(document).on('click', '#st-cart-btn', function (e) {
        if (cmodal) {
            e.preventDefault();
            cmodal.show();
        }

    });

    $('#integration_option').on('change', function() {
      var integration_option  = $(this).val();
      $('.integration-form').fadeOut(200);
      if(integration_option.length > 0) {
        $('.'+integration_option+'-form').fadeIn(300);
      }

    });

    $('.btngooglefeed').on('click', function() {
      var admin_url = $('.admin_url').val();

      $('.loading').fadeIn(100);

      $.ajax({
            type : "POST",
            dataType : "json",
            url : admin_url,
            data : {action: "import_google_feed"},
            success: function(response) {
                alert('Product was successfully imported!');

                $('.loading').fadeOut(100);
               }
       });
    });

    $('.btngoogleconnect').on('click', function() {
      var site_url = $('.site_url').val();
      window.parent.location = site_url + '/google-shopping/google-api.php?site_url='+site_url;

    });

})(jQuery);
